import classes from "./PageContent.module.css";
import { useState } from "react";

import CopyIcon from "./assets/copy.svg";

function PageContent({ title, children }) {
  const [copiedIndex, setCopiedIndex] = useState(null);
  const layoutTransition = {
    type: "spring",
    stiffness: 300,
    damping: 30,
    duration: 1, // increase duration to make it slower
  };
  const copyToClipboard = (e, text, index) => {
    // Check if the Clipboard API is available
    e.preventDefault();
    if (navigator.clipboard) {
      navigator.clipboard
        .writeText(text)
        .then(() => {
          setCopiedIndex(index);
          setTimeout(() => setCopiedIndex(null), 1000);
        })
        .catch((err) => {
          console.error("Failed to copy: ", err);
        });
    } else {
      // Fallback for older browsers or environments without Clipboard API support
      const textArea = document.createElement("textarea");
      textArea.value = text;
      textArea.style.position = "fixed"; // Avoid scrolling to bottom
      textArea.style.top = 0;
      textArea.style.left = 0;
      textArea.style.opacity = 0; // Make it invisible
      document.body.appendChild(textArea);
      textArea.focus();
      textArea.select();

      try {
        const successful = document.execCommand("copy");
        if (successful) {
          setCopiedIndex(index);
          setTimeout(() => setCopiedIndex(null), 1000);
        } else {
          console.error("Fallback: Unable to copy");
        }
      } catch (err) {
        console.error("Fallback: Error copying text", err);
      }

      document.body.removeChild(textArea);
    }
  };
  return (
    <div className={classes.contentBody}>
      <section>
        <h1 className={classes.contentHeader}>Contact</h1>
        <p>Joe_Meehan@OptimaVitaConsulting.com</p>
        <div className={classes.locationNameContainer}>
          <div className={`${classes.location} ${classes.locationName}`}>
            Location
          </div>

          <div
            onClick={(e) => copyToClipboard("joe")}
            className={classes.copyButtonContainer}
          >
            {copiedIndex ? (
              <motion.div className={classes.copiedActionContainer}>
                <motion.div className={classes.copiedActionText}>
                  Copied!
                </motion.div>
              </motion.div>
            ) : (
              <img
                src={CopyIcon}
                alt="copy"
                className={classes.copyButton}
                transition={layoutTransition}
              />
            )}
          </div>
        </div>
      </section>
    </div>
  );
}

export default PageContent;
