                    {typeof e.price === "object"
                      ? "$" +
                        e.price.price_min +
                        "-" +
                        e.price.price_max +
                        "/session"
                      : "$" + e.price + "/session"}
                  </motion.div> */}
                </motion.div>
              )}

              {matches && (
                <NavLink
                  to={e.stripe_link}
                  className={({ isActive }) =>
                    isActive
                      ? `${classes.active} ${
                          classes.subscriptionsPricingCardFooterButton
                        } ${
                          cardFlipState[ind].flipCard &&
                          classes.subscriptionsPricingCardFooterButtonDisabled
                        }`
                      : `${classes.subscriptionsPricingCardFooterButton} ${
                          cardFlipState[ind].flipCard &&
                          classes.subscriptionsPricingCardFooterButtonDisabled
